import React, {useState} from "react";
import { fetchLogin } from "../utility/api";
const Login = (props) =>{
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');
    //const [loginUser, setloginUser] = useState('')
    const {token, setToken} = props


    const handleChangeUser = (event) => {
        setUserName(event.target.value)
    }

    const handleChangePassword = (event) => {
        setPassword(event.target.value)
    }

    const handleSubmit = async (event) =>{
        event.preventDefault();
        const loggedToken = await fetchLogin(userName,password) // fetching login token
        setToken(loggedToken)
        console.log(token)

        setUserName('')
        setPassword('')
    }
// once i log in, I want my username to show and a create a button that sends me to my profile. 

    return <>
    <h1 id="login">Login</h1>
        <div id='form-container'>
            <form id="login-form" onSubmit={handleSubmit}>
                <label htmlFor="Username">Username</label>
                <input type="text" name="username" value={userName} onChange={handleChangeUser} required />
                <label htmlFor="Password">Password</label>
                <input className="password" type="password" name="password" value={password} onChange={handleChangePassword}required />
                <button className="clientButton" type="submit">LOG IN</button>

            </form> 
        </div>  
    </>
}

export default Login
