import React, {useState} from "react";
import { fetchRegister } from "../utility/api"
//{user, setUser, token, setToken};

const Register = () => {
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const [user, setUser] = useState([])

    const RegisteredUser = async () => {
        setUser(await fetchRegister(userName, password))
    }

    const handleChangeUser = (event) => {
            setUserName(event.target.value)
        }

    const handleChangePassword = (event) => {
            setPassword(event.target.value)
        }

    const handleSubmit = (event) =>{
            event.preventDefault();
            RegisteredUser();
            //setUser(registeredUser)
            console.log(userName, password)
            setUserName('')
            setPassword('')
            console.log(RegisteredUser())
        }

    return (
        <div >
             <h1 id= "register">Sign up</h1>
            <div id='form-container'>
                <form id="register-form" onSubmit={handleSubmit}>
                    <label htmlFor="Username">Username</label>
                    <input type="text" name="username" value={userName} onChange={handleChangeUser} required />
                    <label htmlFor="Password">Password</label>
                    <input className="password" type="password" name="password" value={password} onChange={handleChangePassword}required />
                    <button className="clientButton" type="submit">SIGN UP</button>
                </form> 
            </div>  

        </div>

    )

}

export default Register
