import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { fetchUserData } from "../utility/api";

const Profile = ({ token, userName }) => {
    const [sentMessages, setSentMessages] = useState([])
    const [received, setReceived] = useState([])
    const history = useHistory();

    const dataMessage = async () => {
        let userData = await fetchUserData(token);
        setSentMessages(userData.messages)
        console.log(userData)
    }

    const handleClick = () => {
        history.push(`/posts/${userData.posts._id}`)
    }


    useEffect(() => {
        dataMessage();
    }, [])

  return <> 
    {
        !token ? <Link to="/login" id="profile-title">(Please Log In)</Link> : 
       <>
          <h1 className="profile-title">WELCOME {userName}</h1>
          <h2 className="profile-title">Received Messages</h2>
          {
            sentMessages.map(message =>{
                return <>
                    {
                    userName !== message.fromUser.username ?
                    <article id="message-card">
                       <div id="sender">From: {message.fromUser.username}</div>
                       <br></br>
                       <div id="content-message">{message.content} </div>
                       <br></br>
                       <a onClick={handleClick}>View Post</a>
                    </article>

                    : null
                    }

                </>

            })
          }
        </>
    }
    </>
}


export default Profile;
